			}
			if($thetaFirstOccurrence !== false){
				$firstOccuredTagPosition = $thetaFirstOccurrence;
				$applicableCustomTag = 'theta';
			}
			if($degreeFirstOccurrence !== false){
				if($firstOccuredTagPosition == ""){
