				if($asteriskFirstOccurrence !== false){
					$firstOccuredSignPosition = $asteriskFirstOccurrence;
					$applicableCustomTag = $customTag;
				}
				if($degreeFirstOccurrence !== false){
					if($firstOccuredSignPosition == ""){
