	*/
	function custom_in_array($needle, $needleField, $array, $strict = false) {
		if(!empty($array)){
			foreach ($array as $key => $val) {
				if($strict){
					if(isset($array[$key][$needleField]) AND $array[$key][$needleField] === $needle)
						return true;
				}else{
					if(isset($array[$key][$needleField]) AND $array[$key][$needleField] == $needle)
						return true;
				}
		    }	
		}

	    return false;
	}

