			}
			else if(($isCommaExist != 0 OR $isSemicolonExist != 0) AND $isHyphenExist == 0 AND $isAaoExist === false AND $isAaoWithoutSpaceExist === false AND $isAaoAllLowerWithoutSpaceExist === false AND $isAaoAllLowerWithSpaceExist === false)
