	// This function is used in comparison save operations pages.
	function removeExistingQuelleData($savedComparisonQuelleIdArr, $arzneiId)
	{
		global $db;
		global $date;
		$returnArr = array(
			'status' => false,
			'return_data' => array(),
			'message' => 'Operation failed'
		);
