	function teaser( $html ) {
	    $html = str_replace( '&nbsp;', ' ', $html );
	    do {
	        $tmp = $html;
	        $html = preg_replace('#<([^ >]+)[^>]*>[[:space:]]*</\1>#', '', $html );
	    } while ( $html !== $tmp );

	    return $html;
	}

